mapper.update:
UPDATE setmeal
name = #{name}, category_id = #{categoryId}, price = #{price}, status = #{status}, description = #{description}, image = #{image}, update_time = #{updateTime},
id = #{id}

mapper.select:
SELECT s.*, c.name AS categoryName
FROM setmeal s
LEFT JOIN category c ON s.category_id = c.id


        AND s.name like concat('%',#{name},'%')


        AND s.category_id = #{categoryId}


        AND s.status = #{status}
